/*Hacer un programa en C++ que lea dos números y diga cuál es el mayor*/
#include<iostream>

using namespace std;
int main ()
{
 int a,b;
 cout<<"ingresar dos numeros"<<endl;
 cin>>a>>b;
 if (a>b)
 {
  cout<<"el numero mayor es"<<a<<endl;
 }
 else
 {
  cout<<"el numero mayor es"<<b<<endl;
 }
 
 
 
 
}
