/*Hacer un programa en C++ que lea un número entero por el teclado y determinar si
es negativo.*/
#include<iostream>

using namespace std;
int main(){
	int a;
	cout<<"ingresa un numero positivo o negativo"<<endl;
	cin>>a;
	
	if(a>0)
		cout<<"su numero es positivo"<<endl;
	else
		cout<<"su numero es negativo"<<endl;
	



	
}
